INSERT INTO public.annotation (id,created_at,updated_at,user_id,draft_id,start,"end",text,"tag",is_public) VALUES ('b3dc81ca-7163-550a-9f61-48588969b2b3', '2025-06-13T11:32:49.903Z', '2025-06-13T11:32:49.903Z', '84904b59-72e6-54be-8cc2-f6f36c087959', 10, 8, 12, 'In commodum sint eo quod laudanimbut declare sentianusque.', 'Quorum', 'f'), ('9381b988-9731-5e5f-be05-1b6d016bfcdd', '2025-06-13T11:32:49.904Z', '2025-06-13T11:32:49.904Z', '5f2c632c-1003-5d3e-94f5-6085899aa359', 8, 3, 19, 'Cum malorum philos iditura se non, homin si probam erque nec talibus versione.', 'Ceterime', 'f'), ('3026e785-1b9b-5a05-a6b3-7d8378fe2e9d', '2025-06-13T11:32:49.904Z', '2025-06-13T11:32:49.904Z', '5ac46069-d153-5b50-a0be-8a017d379243', 5, 10, 17, 'Expecuna sit inqueret est otio ophiae non.', 'Ad', 'f'), ('231194ff-196b-599f-bead-eac218db53cc', '2025-06-13T11:32:49.904Z', '2025-06-13T11:32:49.904Z', '7694c29f-250e-5568-b2b3-d9dcb7cf3403', 1, 0, 17, 'Non sensuscipit graece horrida voluptas.', 'Um', 't'), ('b6eb8059-ce41-5cc6-922f-ef38c6b1344e', '2025-06-13T11:32:49.904Z', '2025-06-13T11:32:49.904Z', 'a48b587e-6d30-5687-a8c3-52266b42a879', 8, 10, 16, 'Ut mox commodo tari effugiendam cuiquamur, in dicandistrah expetuis convendibil affeci.', 'In', 'f'), ('514171a5-ebc0-5f01-b5af-fad92e469b57', '2025-06-13T11:32:49.905Z', '2025-06-13T11:32:49.905Z', '201a57ad-3e5f-593b-903c-61b025ec68b0', 3, 3, 16, 'Et d laudanim aut im alter voluptatum iudicitum, esset constitut vivi conclusum desid.', 'Reprehent', 't'), ('d269ee89-fd3e-56ec-bab7-964edfd7e624', '2025-06-13T11:32:49.905Z', '2025-06-13T11:32:49.905Z', '5ac46069-d153-5b50-a0be-8a017d379243', 2, 2, 18, 'Delectam tamenimis mortis eos quae.', 'Scriberim', 'f'), ('f7f19cdd-3489-5d91-8e91-b333d57c0568', '2025-06-13T11:32:49.905Z', '2025-06-13T11:32:49.905Z', '598720fe-85ea-5d65-be77-84cde8cedb1e', 6, 9, 11, 'Id sit habet cognominpen quod indus et, ere es sent se perfectus.', 'Ad', 'f'), ('a3bb91fd-a595-5f4c-84ac-e4c53ffb13d5', '2025-06-13T11:32:49.905Z', '2025-06-13T11:32:49.906Z', 'a48b587e-6d30-5687-a8c3-52266b42a879', 9, 8, 18, 'Vivat nec omnes est iditurbes quae, corporis fuisse exmulto sempersequi modo atomnem ego apsa.', 'Maiorest', 'f'), ('527735e4-e082-5cc3-8dd2-ae6833b91caf', '2025-06-13T11:32:49.906Z', '2025-06-13T11:32:49.906Z', '0f5d546a-dd80-5406-a005-a4f3061b9fb4', 7, 4, 20, 'Quem ent aperia quo seditias.', 'Concurate', 'f'), ('3d26a4ca-2b88-5f7a-8bb4-39359802bab3', '2025-06-13T11:32:49.906Z', '2025-06-13T11:32:49.906Z', '0b822969-cf55-5a03-8676-9adebfef1c2b', 6, 6, 16, 'Sine sit autem vim quorum quae sapientia vitamicium, entis aut dixeration qui te voluptas reici ad.', 'Ponitaque', 'f'), ('f6078e17-014d-5196-94a0-54a9244f39d5', '2025-06-13T11:32:49.906Z', '2025-06-13T11:32:49.906Z', '0b822969-cf55-5a03-8676-9adebfef1c2b', 10, 6, 11, 'Modumquam et tantale et opem finit videatur insidua.', 'Il', 'f'), ('8bad9735-9811-5c90-b4e7-bd5945e1d63e', '2025-06-13T11:32:49.906Z', '2025-06-13T11:32:49.906Z', '598720fe-85ea-5d65-be77-84cde8cedb1e', 3, 2, 19, 'In gaudercitus antia provint privendi fuga frequentiam.', 'Direcorian', 'f'), ('3c23745a-1ae7-58f5-8c16-7bf60572b638', '2025-06-13T11:32:49.907Z', '2025-06-13T11:32:49.907Z', '84904b59-72e6-54be-8cc2-f6f36c087959', 5, 0, 16, 'Fabultat magnumquam multi in essit is cum.', 'Quia', 'f'), ('cab29b80-44bb-5cff-a471-3b4a03370f39', '2025-06-13T11:32:49.907Z', '2025-06-13T11:32:49.907Z', '0b822969-cf55-5a03-8676-9adebfef1c2b', 2, 3, 17, 'Sint philos quam etiam et quae quantaque, ignotionis nos imperspicuum o et.', 'Tota', 't'), ('30b2ec85-0b0d-5846-8e2f-d2cc46a43de5', '2025-06-13T11:32:49.907Z', '2025-06-13T11:32:49.907Z', '5ac46069-d153-5b50-a0be-8a017d379243', 3, 10, 15, 'Contingeren nullas ate versariam qui necest eorum nihillo, vel in nosque comprehentes abo.', 'Antium', 't'), ('78affded-be11-5b53-9df8-ce5eeeb687ff', '2025-06-13T11:32:49.907Z', '2025-06-13T11:32:49.907Z', '0f5d546a-dd80-5406-a005-a4f3061b9fb4', 5, 2, 16, 'Quo hanc aut probanimus et est.', 'Nec